@asyncio_run
async def gitingest(
    source: str,
    output: Path | None,
    max_size: int | None,
    exclude_pattern: tuple[str, ...],
    include_pattern: tuple[str, ...],
    branch: str | None,
) -> None:
    """
    Ingests a repository or directory, clones it if needed, then returns the
    directory structure and all file contents (up to the max size limit).
    """
    results = await ingest_async(source, max_size, list(exclude_pattern), list(include_pattern), branch)

    if output:
        output.write_text(results.formatted_content)
    else:
        print(results.formatted_content)
