```filename: src/gitingest/entrypoint.py
async def ingest_async(
    source: str,
    max_size: int | None = None,
    exclude_patterns: list[str] | None = None,
    include_patterns: list[str] | None = None,
    branch: str | None = None,
) -> IngestedData:
    """Ingests a URL or local path. If a URL, it clones it, and then processes it."""
    query = parse_query(source, max_file_size_kb=max_size, include_patterns=include_patterns, ignore_patterns=exclude_patterns, branch=branch)
    return await ingest_query(query)

def ingest(
    source: str,
    max_size: int | None = None,
    exclude_patterns: list[str] | None = None,
    include_patterns: list[str] | None = None,
    branch: str | None = None,
) -> IngestedData:
    """Synchronous wrapper for ingest_async."""
    return asyncio.run(ingest_async(source, max_size, exclude_patterns, include_patterns, branch))
