```filename: src/server/query_processor.py
async def process_query(request: Request, input_text: str, slider_position: int, pattern_type: str, pattern: str, is_index: bool):
    try:
        max_file_size_kb = log_slider_to_size(slider_position)

        query = parse_query(
            input_text,
            max_file_size_kb=max_file_size_kb,
            include_patterns=[pattern] if pattern_type == "include" and pattern else None,
            ignore_patterns=[pattern] if pattern_type == "exclude" and pattern else None,
        )

        clone_config = query.extract_clone_config()
        await clone_repo(clone_config)

        results = await ingest_query(query)

        content_display = results.formatted_content[:server_config.MAX_DISPLAY_SIZE]

        # Render the template with the results
        return templates.TemplateResponse(
            "index.jinja" if is_index else "git.jinja",
            {
                "request": request,
                "summary": results.summary,
                "directory_structure": results.directory_structure,
                "content": content_display,
                "input_text": input_text,
                "is_index": is_index,
            },
        )
    except Exception as e:
        tb_str = traceback.format_exception(type(e), e, e.__traceback__)
        error_message = f"Error processing the repository: {e}\n{''.join(tb_str)}"
        return templates.TemplateResponse(
            "index.jinja" if is_index else "git.jinja",
            {
                "request": request,
                "error": error_message,
                "input_text": input_text,
                "is_index": is_index,
            },
        )
