function handleSubmit(event, showLoading = false) {
    event.preventDefault();

    const form = event.target;
    const formData = new FormData(form);

    // Get the file size limit from the slider
    const sliderPosition = document.getElementById('fileSizeSlider').value;
    const fileSizeInKB = logSliderToSize(sliderPosition);
    formData.set('slider_position', sliderPosition);

    // Get pattern type
    var patternType = document.querySelector('input[name="patternType"]:checked');
    if (patternType) {
        patternType = patternType.value
    } else {
        patternType = "";
    }
    formData.set('pattern_type', patternType);

    // Get pattern
    const pattern = document.getElementById('pattern').value;
    formData.set('pattern', pattern);

    // Show loading animation on submit button
    const submitButton = form.querySelector('button[type="submit"]');
    if (showLoading) {
        submitButton.innerHTML = '<span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span> Loading...';
        submitButton.disabled = true;
    }

    fetch(form.action, {
        method: form.method,
        body: formData
    })
    .then(response => response.text())
    .then(html => {
        // Replace the entire page content with the response from the server
        document.documentElement.innerHTML = html;

        // Re-init the slider
        initializeSlider();
        // Handle scrolling to results after the content is replaced
        const resultsDiv = document.getElementById('results');
        if (resultsDiv) {
            resultsDiv.scrollIntoView({ behavior: 'smooth' });
        }
    })
    .catch(error => {
        console.error('Error:', error);
        // Re-enable the submit button and remove the loading animation
        submitButton.innerHTML = 'Submit';
        submitButton.disabled = false;
    });
}
